mapper.select:
-- 최근 스탠다드 구독 내역 조회 --
select sub_lf.SUB_LF_ID, lf.LF_NAME, lf.LF_ST_COIN, lf.LF_IMG_MAIN, lf_opt.LF_OPT_NAME, lf_brand.LF_BRAND_NAME, subscribe.SUBSCRIBE_START_DATE, subscribe.SUBSCRIBE_END_DATE, subscription_type.SUBSCRIPTION_TYPE_NAME from SUBSCRIPTION subscribe join LFSUBSCRIPTIONLIST sub_lf on sub_lf.SUBSCRIPTION_ID = subscribe.SUBSCRIPTION_ID join LLIFEFURNITURE lf on lf.lf_id = sub_lf.LF_ID join LFURNITUREOPTION lf_opt on lf_opt.LF_OPT_ID = sub_lf.LF_OPT_ID join LFURNITUREBRAND lf_brand on lf.LF_BRAND_ID = lf_brand.LF_BRAND_ID join SUBSCRIPTIONTYPE subscription_type on subscription_type.SUBSCRIPTION_TYPE_ID = subscribe.SUBSCRIBE_TYPE_ID
where subscribe.M_ID = 1 and subscribe.SUBSCRIBE_START_DATE < #{endDate}
