mapper.select:
SELECT lf.lf_id, lf.lf_img_main, lfb.lf_brand_name, lf.lf_name, lf.lf_pr_price, lfb.lf_brand_id FROM LLifeFurniture lf JOIN LFurnitureBrand lfb ON lf.lf_brand_id = lfb.lf_brand_id JOIN LFurnitureMood lfm ON lf.lf_mood_id = lfm.lf_mood_id WHERE lf.lf_sub_type = 1
AND lfb.lf_brand_id IN
#{brandId}
AND lfm.lf_mood_id IN
#{moodId}
AND lf.lf_pr_price BETWEEN #{minPrice} AND #{maxPrice}
OFFSET #{offset} ROWS FETCH NEXT 20 ROWS ONLY

SELECT s.lf_opt_id, s.lf_id, s.stock_amount
FROM Stock s
WHERE s.lf_opt_id = #{lfOptId}
AND s.lf_id = #{lfId}
