mapper.select:
SELECT a.*, b.*, c.*
FROM Furniture a
JOIN FurnitureCategory b ON a.f_category_id = b.${fCategoryId}
JOIN LLifeFurniture c ON a.f_id = c.${fId}
WHERE b.f_category_name = ${fCategoryName}

SELECT a.*, b.*, c.*
FROM Furniture a
JOIN FurnitureBrand b ON a.f_brand_id = b.${fBrandId}
JOIN LLifeFurniture c ON a.f_id = c.${fId}
WHERE b.f_brand_name = ${fBrandName}

SELECT a.*, b.*, c.*
FROM Furniture a
JOIN FurnitureMood b ON a.f_mood_id = b.${fMoodId}
JOIN LLifeFurniture c ON a.f_id = c.${fId}
WHERE b.f_mood_name = ${fMoodName}
